import React, { useState } from "react";
import * as sessionActions from "../../store/session";
import { useDispatch } from "react-redux";
import './LoginForm.css';


function LoginForm() {
  const dispatch = useDispatch();
  const [credential, setCredential] = useState("");
  const [password, setPassword] = useState("");
  const [errors, setErrors] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setErrors([]);
    return dispatch(sessionActions.login({ credential, password })).catch(
      async (res) => {
        const data = await res.json();
        if (data && data.errors) setErrors(data.errors);
      }
    );
  };

  return (
    <>
      <div className="header">
        <span>Kelp [LOGO] </span>
      </div>
      <form onSubmit={handleSubmit}>
        <div className="errors">
          <ul>
            {errors.map((error, idx) => (
              <li key={idx}>{error}</li>
            ))}
          </ul>
        </div>
        <div className="modal">
          <div className="modal-content">
            <h4>Log in to Kelp</h4>
            <p className="login-info">
              New to Yelp? Sign up By logging in, you agree to Yelp’s Terms of Service and Privacy Policy.
            </p>
            <div className="third-party-logins">
              <div className="facebook">
                <button className="facebook-btn">Continue with Facebook</button>
              </div>
              <div className="google">
                <button className="google-btn">Continue with Google</button>
              </div>
              <div className="appl">
                <button className="apple-btn">Continue with Apple</button>
              </div>
            </div>
            <div className="or">
              <span>OR</span>
            </div>
            <div className="login-container">
              <div className="user-input">
                <input
                  type="text"
                  value={credential}
                  onChange={(e) => setCredential(e.target.value)}
                  required
                  placeholder="Email"
                />
                </div>
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                  placeholder="Password"
                />

              <button type="submit">Log In</button>
            </div>
          </div>
        </div>
      </form>
    </>
  );
}

export default LoginForm;
